import React, { useState } from "react";
import { Section } from "./Section/Section.jsx";
import { Notification } from "./Notification/Notification.jsx";
import { Statistics } from "./Statistics/Statistics";
import { FeedbackOptions } from "./FeedbackOptions/FeedbackOptions";

const App = () => {
  const [feedback, setFeedback] = setState({ good: 0, neutral: 0, bad: 0 });

  const handleIncrement = (evt) => {
    const { name } = evt.target;
    setFeedback((prevState) => ({ ...prevState, [name]: prevState[name] + 1 }));
  };

  const countTotalFeedback = () => {
    const { good, neutral, bad } = feedback;
    return good + neutral + bad;
  };

  const countPositiveFeedbackPercentage = () => {
    const { good, neutral, bad } = feedback;
    const summary = good + neutral + bad;
    if (summary === 0) {
      return 0;
    }
    return Math.round((good / summary) * 100);
  };

  const total = countTotalFeedback();
  const positivePercentage = countPositiveFeedbackPercentage();

  return (
    <div>
      <Section title="Please leave your feedback">
        <FeedbackOptions onLeaveFeedback={handleIncrement} />
        {total === 0 ? (
          <Notification message="There is no feedback"></Notification>
        ) : (
          <Statistics
            good={feedback.good}
            neutral={feedback.neutral}
            bad={feedback.bad}
            total={total}
            positivePercentage={positivePercentage}
          />
        )}
      </Section>
    </div>
  );
};
